emController@store');        Route::post('menu-items/toggle', 'MenuItemController@toggle');        Route::delete('menu-items/batch', 'MenuItemController@batchDestroy');        //优惠券        Route::apiResource('coupons', 'CouponController');        //徽章        Route::apiResource('badges', 'BadgeController');    });    //用户管理    Route::namespace('User')->group(function () {        Route::apiResource('user-groups', 'GroupController');        Route::get('users/options', 'UserController@options');        Route::apiResource('users', 'UserController');        Route::get('user/info', 'UserController@info');        Route::post('users/{id}/points', 'UserController@updatePoints');        Route::apiResource('users/points/transactions', 'PointTransactionController');        Route::apiResource('roles', 'RoleController');    });    //页面    Route::namespace('Page')->group(function () {        Route::apiResource('pages', 'PageController');    });    //分类    Route::namespace('Category')->group(function () {        Route::apiResource('categories', 'CategoryController');        Route::post('categories/{id}/increase', 'CategoryController@increase')->middleware('auth:api');        Route::post('categories/{id}/decrease', 'CategoryController@decrease')->middleware('auth:api');    });    //文章管理    Route::namespace('Post')->middleware(['auth:api'])->group(function () {        Route::get('posts/options', 'PostController@options');        Route::apiResource('posts', 'PostController');    });    //微信管理    Route::name('wechat.')->namespace('Wechat')->middleware('auth:api')->prefix('wechat')->group(function () {        Route::apiResource('menus', 'MenuController');        Route::get('menu/types', 'MenuController@types');        Route::post('menu/apply', 'MenuController@apply');        Route::get('material/image', 'MaterialController@image');    });    //电子商务    Route::namespace('Ecommerce')->middleware('auth:api')->group(function () {        //商品属性        Route::apiResource('products/attributes', 'AttributeController');        //产品变量        Route::apiResource('products/variations', 'VariationController');        //商品管理        Route::apiResource('products', 'ProductController');        Route::post('products/batch', 'ProductController@batchUpdate');        Route::post('products/adjust', 'ProductController@batchAdjust');        //店铺管理        Route::apiResource('shops', 'ShopController');        Route::post('shops/{id}/verify', 'ShopController@verify')->middleware('auth:api');        //运费模版        Route::apiResource('freight-templates', 'FreightTemplateController');        //配送区域        Route::apiResource('shipping-zones', 'ShippingZoneController');        //订单管理        Route::get('orders/options', 'OrderController@options');        Route::apiResource('orders', 'OrderController');        Route::get('orders/{id}/notes', 'OrderController@notes');        Route::get('orders/{id}/print', 'OrderController@printOrder');        //照片墙        Route::apiResource('photo-walls', 'PhotoWallController');        //配送员        Route::get('deliveryers/{id}/billing', 'DeliveryerController@billing');        Route::apiResource('deliveryers', 'DeliveryerController');        Route::get('deliveryers/{id}/orders', 'DeliveryerController@orders');        Route::get('deliveryers/{id}/caculate', 'DeliveryerController@calculateFee');        Route::get('deliveryers/transactions/billing', 'DeliveryerTransactionController@billing');        Route::apiResource('deliveryers.transactions', 'DeliveryerTransactionController');        //pos机管理        Route::apiResource('pos-machines', 'PosMachineController');        //收银台账        Route::get('cashier/transactions/billing', 'CashierTransactionController@billing');        Route::apiResource('cashier/transactions', 'CashierTransactionController');    });    Route::namespace('Lottery')->prefix('lottery')->group(function () {        Route::get('settings', 'SettingController@index');        Route::post('settings', 'SettingController@update');        Route::apiResource('records', 'RecordController');        Route::apiResource('prizes', 'PrizeController');    });});